import React from 'react';
import ReactDOM from 'react-dom';
import PropTypes from 'prop-types';
import './index.css';
import 'font-awesome/css/font-awesome.min.css';
//import logo from './goblin.png'
//import moment from 'moment';

const FileList = ({ files }) => (
<table className="filelist"><tbody>
{files.map(file =>
/* now we use FileListItem here */
<FileListItem key={file.id} file={file}/>
)}
</tbody>
</table>
);
FileList.propTypes = {
files: PropTypes.array
};

const testFiles = [{
id: 1,
name: 'src',
type: 'folder',
updated_at: "2016-07-11 21:24:00",
latestCommit: {
message: 'Initial commit'
}
},
{
id: 2,
name: 'tests',
type: 'folder',
updated_at: "2016-07-11 21:24:00",
latestCommit: {
message: 'Initial commit'
}
},
{
id: 3,
name: 'README',
type: 'file',
updated_at: "2016-07-18 21:24:00",
latestCommit: {
message: 'Added a readme'
}
},
];

const FileListItem = ({ file }) => (
<tr className="filelist-item">
{getFileName(file)}
<CommitMessage commit={file.latestCommit} />
<td className="age"><Time time={file.updated_at}/></td>
</tr>
);

FileListItem.propTypes = {
file: PropTypes.object.isRequired
};

function FileIcon({ file }) {
let icon = 'fa-file-text-o';
if(file.type === 'folder') {
icon = 'fa-folder';
}
return (
<td className="file-icon">
<i className={`fa ${icon}`}/>
</td>
);
}
FileIcon.propTypes = {
file: PropTypes.object.isRequired
};

function getFileName(file) {
return [
<FileIcon file={file} key={0}/>,
<td className="file-name" key={1}>{file.name}</td>
];
}

const CommitMessage = ({ commit }) => (
<td className="commitmessage">
{commit.message}
</td>
);
CommitMessage.propTypes = {
commit: PropTypes.object.isRequired
};

const Time = ({ time }) => {
  const timeString = time;//moment(time).fromNow();
  return (<span className="time">{timeString}</span>);
};

Time.propTypes = {
time: PropTypes.string
};

ReactDOM.render(
  <FileList files={testFiles}/>,
  document.querySelector("#root")
);

